function detectAndSetLanguage() {
    const browserLang = navigator.language || navigator.userLanguage;
    const langCode = browserLang.split('-')[0].toLowerCase();
    
    // ì§€ì›í•˜ëŠ” ì–¸ì–´ì¸ì§€ í™•ì¸
    if (texts[langCode]) {
        return langCode;
    } else {
        return 'en'; // ê¸°ë³¸ê°’ì€ ì˜ì–´
    }
}

